import React, {useState} from "react";
import './Calculator.css';

function Calculator(){
const [number1, setNumber1]=useState("");
const [number2, setNumber2]=useState("");
const [results, setresults]=useState(0);

const onAdd=(e)=>{
    e.preventDefault();
   const sumx=+number1 + +number2;
setresults(sumx)
};

const onSub=(e)=>{
    e.preventDefault();
   const sub=number1-number2;
setresults(sub)
};

const onMulp=(e)=>{
    e.preventDefault();
   const mul=number1* number2;
setresults(mul)
};

const onDiv=(e)=>{
    e.preventDefault();
   const div=number1 / number2;
setresults(div)
};
const onPer=(e)=>{
    e.preventDefault();
   const Per=number1 / number2*100 ;
setresults(Per)
};
const onClear=(e)=>{
    e.preventDefault();
   setNumber1("")
   setNumber2("")
};
const onDel=(e)=>{
    e.preventDefault();
   setresults(0)
};









return (
    <div>
  <div class="ui three column grid">


  <div class="column">
    <div class="d1 ui segment"> 
    </div>
  </div>


  <div class="c2 column">
    <div class="d2 ui segment">
        <div className="headingDiv"><h1><i class="calculator icon"></i> CALCULATOR</h1></div>

   <form className="ui form">
  <div className="field">
    <label>Enter 1st No.</label>
    <input type="number" 
     placeholder="First Number"
     value={number1}
     onChange={(e)=>{ setNumber1(e.target.value)}}/>
  </div>

  <div className="field">
    <label>Enter 2nd No.</label>
    <input type="number" 
     placeholder="2nd Number"
     value={number2}
     onChange={(e)=>{ setNumber2(e.target.value)}}/>  
  </div>
<div className="buttonSection">
   <button className="ui button" onClick={onAdd} ><i class="plus icon"></i></button>
   <button className="ui button" onClick={onSub} ><i class="minus icon"></i></button>
   <button className="ui button" onClick={onMulp} >X</button>
   <button className="ui button" onClick={onDiv} >Div</button>
   <button className="ui button" onClick={onPer} >%</button>
   <button className="ui button" onClick={onClear}  >clear</button>
</div>
<div className="resultDiv">{results}</div>
  
  <div className="delButton">
  <button className="ui button" onClick={onDel}  ><i class="archieve icon"></i></button>
  </div>

  </form>
    </div>
  </div>



  <div class="column">
    <div class="d3 ui segment">
    </div>
  </div>




</div>
    </div>
    
)  
};
export default Calculator;
