import React, {useState} from "react";
import './Calculator.css';

function Calculator(){
const [number1, setNumber1]=useState("");
const [number2, setNumber2]=useState("");
const [results, setresults]=useState(0);

const onAdd=(e)=>{
    e.preventDefault();
   const sumx=+number1 + +number2;
setresults(sumx)
};

const onSub=(e)=>{
    e.preventDefault();
   const sub=number1-number2;
setresults(sub)
};

const onMulp=(e)=>{
    e.preventDefault();
   const mul=number1* number2;
setresults(mul)
};

const onDiv=(e)=>{
    e.preventDefault();
   const div=number1 / number2;
setresults(div)
};
const onPer=(e)=>{
    e.preventDefault();
   const Per=number1 / number2*100 ;
setresults(Per)
};
const onClear=(e)=>{
    e.preventDefault();
   setNumber1("")
   setNumber2("")
};
const onDel=(e)=>{
    e.preventDefault();
   setresults(0)
};

return <div className="mainDiv">


<form className="ui input">
  <div className="field">
    <label>First Number</label>
    <input type="number" 
     placeholder="First Number"
     value={number1}
     onChange={(e)=>{ setNumber1(e.target.value)
}}
   />
  </div>
  <div className="ui input">
    <label>2nd Number</label>
    <input type="number" 
     placeholder="2nd Number"
     value={number2}
     onChange={(e)=>{ setNumber2(e.target.value)
}}
     />
  </div>
  <button className="ui button" onClick={onAdd} >Add</button>
   <button className="ui button" onClick={onSub} >sub</button>
   <button className="ui button" onClick={onMulp} >Mulp</button>
   <button className="ui button" onClick={onDiv} >Div</button>
   <button className="ui button" onClick={onPer} >%</button>
   <button className="ui button" onClick={onClear}  >clear</button>
  </form>
  {results}
  <div>
  <button className="ui button" onClick={onDel}  >DEL</button>
  </div>
  
    </div>;
  
    
};
export default Calculator;
