import React, {useState} from "react";

function Calculator(){
const [number1, setNumber1]=useState("");
const [number2, setNumber2]=useState("");
const [results, setresults]=useState("");

const onAdd=(e)=>{
    e.preventDefault();
   const sumx=+number1 + +number2;
setresults(sumx)
};

const onSub=(e)=>{
    e.preventDefault();
   const sub=number1-number2;
setresults(sub)
};

const onMulp=(e)=>{
    e.preventDefault();
   const mul=number1* number2;
setresults(mul)
};

return <div>
<form className="ui form">
  <div className="field">
    <label>First Number</label>
    <input type="number" 
     placeholder="First Number"
     value={number1}
     onChange={(e)=>{ setNumber1(e.target.value)
}}
   />
  </div>
  <div className="field">
    <label>2nd Number</label>
    <input type="number" 
     placeholder="2nd Number"
     value={number2}
     onChange={(e)=>{ setNumber2(e.target.value)
}}
     />
  </div>
  <button className="ui button"type="submit" onClick={onAdd} >Add</button>
   <button className="ui button" type="submit" onClick={onSub} >sub</button>
   <button className="ui button" type="submit" onClick={onMulp} >Mulp</button>
  </form>
  {results}
    </div>;
  
    
};
export default Calculator;
